import { useState } from "react";

export default function Demo(){
    const [state,setState] = useState({count: 0})
    const addFuc = ()=>{
        const {count} = state;
        setState({count:count+1},()=>{
            console.log("当前的和为：",state.count)
        })
    }
    return(
        <div>
            <h2>当前和为:{state.count}</h2>
            <button onClick={addFuc}>点我加1</button>
        </div>
    )
}
