import React, { createContext } from "react"
import "./index.css"

function B(props){
    return (
      <div className="child">
        <h3>我是B组件</h3>
        <h4>我从A组件接受到的用户名是：{props.username}</h4>
        <C />
      </div>
    );
}
function C(){
    return (
      <div className="grand">
        <h3>我是C组件</h3>
        <MyContext.Consumer>
            {
                value =>{
                    h3
                }
            }
        </MyContext.Consumer>
      </div>
    );
}
const MyContext = createContext();
export default function A(){

    const state = {username:'Tom'}

    return(
        <div className="parent">
            <h3>我是A组件</h3>
            <h4>我的用户名是：{state.username}</h4>
            <MyContext.Provider value={state.username}>
                <B />
            </MyContext.Provider>
        </div>
    )
}
