import React, { Component } from 'react'

export default class Demo extends Component {
  state = {count:0};

  add = ()=>{
    //1.获取原来的count值
    const {count} = this.state
    //2.更新状态
    this.setState({count: count+1}, ()=>{
        console.log(this.state.count);
    })
  }

    render() {
    return (
      <div>
        <h2>当前的和为{this.state.count}</h2>
        <button>点我</button>
      </div>
    )
  }
}
